title = #{title}, content = #{content}, address = #{address},
phone = #{phone}, time = #{time}, img = #{img}, map = #{map};

mapper.update:
UPDATE board_trip
title = #{title}, content = #{content}, address = #{address}, phone = #{phone}, time = #{time}, img = #{img}, map = #{map}
WHERE tnum = #{tnum};
